import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import App from "../App";
import Todos from "../components/Todos/Todos";
export const AppContext = React.createContext();

export default function AppContextfile() {
  const [todos, setTodos] = useState([]);

  const [toasts, setToasts] = useState([]);
  const [showEmpty, setShowEmpty] = useState(true);
  const [showBigSpinner, setShowBigSpinner] = useState(false);
  const [searchShow, setSearchShow] = useState(false);
  const [flag, setFlag] = useState("all");
  const [search, setSearch] = useState("");
  const [dataCount, setDataCount] = useState(0);
  const [splash, setSplash] = useState(true);

  //flag
  const flagHandler = (e) => {
    if (flag !== e) {
      setFlag(e);
    }
  };

  //search value
  const searchvalue = (e) => {
    if (e.length > 2) {
      setTimeout(() => {
        setShowBigSpinner(true);
      }, 500);
      setTimeout(() => {
        setSearch(e);
        setShowBigSpinner(false);
      }, 1500);
    }
  };

  //search toggle
  const SearchToggle = () => {
    if (search !== "") {
      setShowBigSpinner(true);
      setTimeout(() => {
        setSearch("");
        setShowBigSpinner(false);
      }, 500);
    }
    setSearchShow(!searchShow);
  };
  const handleRemoveTodo = async (id) => {
    try {
      // eslint-disable-next-line
      const { data } = await supabase
        .from("ReactTodo")
        .delete()
        .match({ id: id });
      let newToast = {
        id: uuidv4(),
        type: "success",
        message: "Task Deleted",
      };
      setDataCount(dataCount - 1);
      setToasts([...toasts, newToast]);
    } catch (error) {
      let newToast = {
        id: uuidv4(),
        type: "error",
        message: error,
      };
      setToasts([...toasts, newToast]);
    }
    removeCompleteFromIncomplete(id);
    if (dataCount === 1) {
      setSearchShow(false);
      setFlag("all");
    }
  };
  const removeCompleteFromIncomplete = (id) => {
    const filtertodos = todos.filter((todo) => todo.id !== id);
    setTodos(filtertodos);
  };
  return (
    <div>
      <AppContext.Provider
        value={{
          todos,
          flag,
          flagHandler,
          toasts,
          setToasts,
          handleRemoveTodo,
          dataCount,
          setDataCount,
          setShowEmpty,
          removeCompleteFromIncomplete,
          searchvalue,
          SearchToggle,
          searchShow,
          setShowBigSpinner,

          search,
          setTodos,
          splash,

          setSplash,

          showBigSpinner,
          setShowBigSpinner,
          showEmpty,
        }}
      >
        <Todos />
        <App />
      </AppContext.Provider>
    </div>
  );
}
