/* eslint-disable array-callback-return */

import "./App.css";

import React, { useContext, useEffect } from "react";
import Todos from "./components/Todos/Todos";
import Header from "./components/Header/Header";
import { Icon } from "./components/Icon/Icon";
import { Tag } from "./components/Tag/Tag";
import { Toaster } from "./components/Toaster/Toaster";
import { supabase } from "./config/apiClient";
import { AppContext } from "./contexts/AppContext";
function App() {
  //splash useEffect
  /* useEffect(() => {
    setTimeout(() => {
      setSplash(false);
    }, 1000);
  }, []); */
  // useEffect
  const {
    todos,
    flag,
    search,
    setTodos,
    splash,
    setDataCount,
    setSplash,
    toasts,
    setToasts,
    dataCount,
    searchvalue,
    SearchToggle,
    searchShow,
    showBigSpinner,
    setShowBigSpinner,
    showEmpty,
  } = useContext(AppContext);

  //Toast useeffect
  useEffect(() => {
    if (toasts.length > 0) {
      setTimeout(() => {
        let allToast = [...toasts];
        allToast.shift();
        setToasts(allToast);
      }, 500);
    }
  }, [toasts, setToasts]);

  return (
    <div className="App">
      {splash && (
        <div className="splash faddingOut">
          <div className="splashLogo">
            <div className="splashIconLogo">
              <Icon src="SplashLeaf" />
            </div>
            <div>
              <Icon src="SplashTitle" />
            </div>
          </div>
        </div>
      )}
      {!splash && (
        <div>
          <Header
            SearchToggle={SearchToggle}
            searchShow={searchShow}
            searchvalue={searchvalue}
            dataCount={dataCount}
          />
          <div className="overToaster">
            <div className="toaster">
              {toasts.length > 0 &&
                toasts.map((toast) => (
                  <Toaster
                    key={toast.id}
                    className={`faddingOut toast toast--visible toast--${toast.type}`}
                  >
                    {toast.type === "success" && <Icon src="Check" />}{" "}
                    {toast.message}
                  </Toaster>
                ))}
            </div>
          </div>
          <div className={`${showBigSpinner && "blur"}`}>
            <Todos />
          </div>

          <div>
            {showBigSpinner && (
              <Icon className="spinning rotateFull" src="Spin" />
            )}
          </div>

          {showEmpty && dataCount === 0 && !showBigSpinner && (
            <div className={`emptyScreenOver `}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd">
                You didn't add any task. Please, add one.
              </Tag>
            </div>
          )}
          {showEmpty && dataCount > 0 && todos.length === 0 && !showBigSpinner && (
            <div className={`emptyScreenOver `}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd"> There is no data for {flag}</Tag>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
