import React from "react";
import searchIcon from "./images/search.svg";
import Leaf from "./images/leaf_1.svg";
import title from "./images/Todos.svg";
const Header = ({
  SearchToggle,
  searchShow,
  searchvalue,
  showBigSpinner,
  todoLength,
}) => {
  return (
    <div className={`${showBigSpinner && "blur"} `}>
      <div className="overHead">
        <div className="Header">
          <div className="logo">
            <div className="imgLogo">
              <img src={Leaf} alt="logo" />
            </div>
            <div>
              <img src={title} alt="title" />
            </div>
          </div>
          <div className="searchInputButton">
            {searchShow && (
              <input
                id="search"
                autoFocus
                className="search btn"
                onChange={(e) => searchvalue(e.target.value)}
              ></input>
            )}
            <button
              className="boxedButton btn"
              disabled={`${todoLength === 0 ? true : false}`}
              onClick={SearchToggle}
            >
              <img src={searchIcon} alt="search" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
