import React, { useState } from "react";
import Todo from "./Todo";
import { Button, TextArea, Icon } from "./App";
const todos = ({
  todos,
  onDelete,
  completeHandler,
  show,
  togglehandler,
  addhandler,
  task,
  showSpinner,
  showBigSpinner,
  flagHandler,
  todoLength,
  editToggle,
  setTodos,
}) => {
  // eslint-disable-next-line
  const [top, setTop] = useState(12);
  // eslint-disable-next-line
  const [showFullSpinner, setShowFullSpinner] = useState(false);
  const moreValue = (e) => {
    setShowFullSpinner(true);
    setTimeout(() => {
      setTop(top + 12);

      setShowFullSpinner(false);
    }, 3000);
  };
  const showless = (e) => {
    setShowFullSpinner(true);
    setTimeout(() => {
      setTop(12);

      setShowFullSpinner(false);
    }, 3000);
  };
  return (
    <div>
      <div className={`${(showBigSpinner || showFullSpinner) && "blur"}`}>
        <div className="addTask">
          <h1 className="addTaskH1">Add Tasks</h1>
        </div>

        <div className="headerClass">
          <Button className="create btn" onClick={togglehandler}>
            <Icon src="Plus" />
            Create
          </Button>
          <div>
            <Button
              className={`topButton btn ${todoLength === 0 && "blurButton"}`}
              disabled={todoLength === 0}
              onClick={(e) => {
                flagHandler("all");
              }}
            >
              All
            </Button>
            <Button
              className={`topButton btn ${todoLength === 0 && "blurButton"}`}
              disabled={todoLength === 0}
              onClick={(e) => {
                flagHandler("incomplete");
              }}
            >
              Incomplete
            </Button>
            <Button
              className={`topButton btn ${todoLength === 0 && "blurButton"}`}
              disabled={todoLength === 0}
              onClick={(e) => {
                flagHandler("complete");
              }}
            >
              Complete
            </Button>
          </div>
        </div>
        <div className="todos">
          {show && (
            <div id="todo" className="todo">
              <div className={`${showSpinner && "blur"}`}>
                <TextArea
                  className="editName"
                  placeholder="Add new task"
                  autoFocus
                  onChange={(e) => task(e.target.value)}
                  readOnly={showSpinner}
                  onKeyPress={(e) => {
                    if (e.key === "Enter") {
                      e.preventDefault();
                      addhandler(e);
                    }
                  }}
                  onFocus={function (e) {
                    var val = e.target.value;
                    e.target.value = "";
                    e.target.value = val;
                  }}
                />
                <Button className="saveButton btn" onClick={addhandler}>
                  Add Task
                </Button>
              </div>
              <div>
                {showSpinner && (
                  <Icon className="spinning rotateDiv" src="Spin" />
                )}
              </div>
            </div>
          )}
          {todos.slice(0, top).map((todo) => (
            <Todo
              allTodos={todos}
              todo={todo}
              key={todo.id}
              onDelete={onDelete}
              completeHandler={completeHandler}
              editToggle={editToggle}
              setTodos={setTodos}
            />
          ))}
        </div>
        <div>
          {showFullSpinner && (
            <Icon className="spinning rotateFull" src="Spin" />
          )}
        </div>
        {!showFullSpinner && (
          <div className="bottomButton">
            {todoLength <= 12 ? null : top <= todoLength ? (
              <div>
                <Button className="loadMoreBtnContainer" onClick={moreValue}>
                  Show More
                </Button>
              </div>
            ) : (
              <div>
                <Button className="loadMoreBtnContainer" onClick={showless}>
                  Show Less
                </Button>
              </div>
            )}
          </div>
        )}
      </div>
      <div>
        {showFullSpinner && <Icon className="spinning rotateFull" src="Spin" />}
      </div>
    </div>
  );
};

export default todos;
