import React from "react";
import Todo from "./Todo";
import plus from "./images/plus.svg";
import spin from "./images/bigspin.svg";
import { Button } from "./App";
const todos = ({
  todos,
  onDelete,
  completeHandler,
  show,
  togglehandler,
  addhandler,
  task,
  showSpinner,
  showBigSpinner,
  flagHandler,
  todoLength,
  editToggle,
}) => {
  return (
    <div className={`${showBigSpinner && "blur"}`}>
      <div className="addTask">
        <h1 className="addTaskH1">Add Tasks</h1>
      </div>

      <div className="headerClass">
        <button className="create btn" onClick={togglehandler}>
          <img src={plus} alt="Create" /> Create
        </button>
        <div>
          <button
            className={`topButton btn ${todoLength === 0 && "blurButton"}`}
            disabled={todoLength === 0}
            onClick={(e) => {
              flagHandler("all");
            }}
          >
            All
          </button>
          <button
            className={`topButton btn ${todoLength === 0 && "blurButton"}`}
            disabled={todoLength === 0}
            onClick={(e) => {
              flagHandler("incomplete");
            }}
          >
            Incomplete
          </button>
          <button
            className={`topButton btn ${todoLength === 0 && "blurButton"}`}
            disabled={todoLength === 0}
            onClick={(e) => {
              flagHandler("complete");
            }}
          >
            Complete
          </button>
        </div>
      </div>
      <div className="todos">
        {show && (
          <div id="todo" className="todo">
            <div className={`${showSpinner && "blur"}`}>
              <textarea
                className="editName"
                placeholder="Add new task"
                autoFocus
                onChange={(e) => task(e.target.value)}
                readOnly={showSpinner}
                onKeyPress={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addhandler(e);
                  }
                }}
                onFocus={function (e) {
                  var val = e.target.value;
                  e.target.value = "";
                  e.target.value = val;
                }}
              />
              <button className="saveButton btn" onClick={addhandler}>
                Add Task
              </button>
            </div>
            <div>
              {showSpinner && (
                <img className="spinning rotateDiv" src={spin} alt="spin" />
              )}
            </div>
          </div>
        )}
        {todos.map((todo) => (
          <Todo
            todo={todo}
            key={todo.id}
            onDelete={onDelete}
            completeHandler={completeHandler}
            editToggle={editToggle}
          />
        ))}
      </div>
    </div>
  );
};

export default todos;
