import "./App.css";
import { useState } from "react";
import Tasks from "./Todos";
function App() {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      name: "Hello",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
    },
    {
      id: 2,
      name: "Hola",
      created_at: "2022-03-31",
      completed_on: null,
    },
  ]);

  // Delete task
  const deletetask = (id) => {
    setTasks();
  };

  return (
    <div className="App">
      <Tasks tasks={tasks} onDelete={deletetask} />
    </div>
  );
}

export default App;
