import { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { Button, TextArea, Icon, Tag } from "./App";
const Todo = ({
  todo,
  onDelete,
  completeHandler,
  editToggle,
  setTodos,
  allTodos,
  toasts,
  setToasts,
}) => {
  const [newName, setNewName] = useState(todo.name);
  const edit = (e) => {
    setNewName(e.target.value);
  };
  const [showLoading, setShowLoading] = useState(false);
  const editValue = (id) => {
    const index = allTodos.findIndex((todo) => todo.id === id);
    console.log(allTodos[index].name);
    console.log(newName);
    if (newName.length > 2 && allTodos[index].name !== newName) {
      // const index = allTodos.findIndex((todo) => todo.id === id);
      let tempTodos = [...allTodos];
      tempTodos[index].name = newName;
      setTodos(tempTodos);
      let newToast = {
        id: uuidv4(),
        type: "success",
        message: "Task Edited",
      };
      let allToast = [...toasts];
      allToast.push(newToast);
      setToasts(allToast);
      editToggle(id);
      setShowLoading(true);
      setTimeout(() => {
        setShowLoading(false);
      }, 1000);
    } else editToggle(id);
  };
  return (
    <div id="todo" className="todo">
      {/* <div className={`${(todo.isLoading || showLoading) && "blur"}`}> */}
      {todo.isEdit === true ? (
        <TextArea
          id="editName"
          className="editName"
          value={newName}
          onChange={edit}
          autoFocus
          onFocus={(e) => {
            var val = e.target.value;
            e.target.value = "";
            e.target.value = val;
          }}
          onKeyPress={(e) => {
            if (e.key === "Enter") {
              e.preventDefault();
              editValue(todo.id);
            }
          }}
        />
      ) : (
        <div>
          <Tag
            id="showName"
            className={`${todo.completed_on && "completed"} ${
              (todo.isLoading || showLoading) && "blur"
            }`}
          >
            {todo.name}
          </Tag>
        </div>
      )}
      <div className="createdAt">
        <div>
          <Tag>Created At: {todo.created_at}</Tag>
        </div>
      </div>
      <div className="boxedButtonCompletedOn">
        <div className="allBoxedButon">
          {todo.isEdit && (
            <Button
              className="saveButton btn"
              onClick={() => editValue(todo.id)}
            >
              Save
            </Button>
          )}
          {!todo.completed_on && (
            <Button
              className="boxedButton "
              onClick={() => {
                completeHandler(todo.id);
              }}
            >
              <Icon src="Tick" />
            </Button>
          )}
          {!todo.completed_on && !todo.isEdit && (
            <Button
              onClick={() => editToggle(todo.id)}
              className="boxedButton "
            >
              <Icon src="Edit" />
            </Button>
          )}
          <Button className="boxedButton" onClick={() => onDelete(todo.id)}>
            <Icon src="Delete" />
          </Button>
        </div>
        {todo.completed_on && (
          <Tag className="completedOn">
            completed in{" "}
            {Math.ceil(
              Math.abs(
                Date.parse(todo.completed_on) - Date.parse(todo.created_at),
              ) /
                (1000 * 60 * 60 * 24),
            )}{" "}
            days
          </Tag>
        )}
      </div>
      {/* </div> */}
      {(todo.isLoading || showLoading) && (
        <Icon className="spinning rotateDiv" src="Spin" />
      )}
    </div>
  );
};

export default Todo;
