import { useState } from "react";
import { Button, TextArea, Icon, Tag } from "./App";
const Todo = ({ todo, onDelete, completeHandler, editToggle, setTodos }) => {
  const [newName, setNewName] = useState(todo.name);
  const edit = (e) => {
    setNewName(e.target.value);
  };
  const editValue = (id) => {
    if (newName.length > 2) {
      let tempTodos = todo;
      tempTodos.name = newName;
      setTodos(tempTodos);
    }
  };
  return (
    <div id="todo" className="todo">
      <div className={`${todo.isLoading && "blur"}`}>
        {todo.isEdit === true ? (
          <TextArea
            id="editName"
            className="editName"
            value={newName}
            onChange={edit}
            autoFocus
            onFocus={(e) => {
              var val = e.target.value;
              e.target.value = "";
              e.target.value = val;
            }}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                e.preventDefault();
                editValue(todo.id);
              }
            }}
          />
        ) : (
          <div>
            <Tag
              id="showName"
              className={`${todo.completed_on && "completed"}`}
            >
              {todo.name}
            </Tag>
          </div>
        )}
        <div className="createdAt">
          <div>
            <Tag>created_at: {todo.created_at}</Tag>
          </div>
        </div>
        <div className="boxedButtonCompletedOn">
          <div className="allBoxedButon">
            {todo.isEdit && (
              <Button
                className="saveButton btn"
                onClick={() => editValue(todo.id)}
              >
                Save
              </Button>
            )}
            {!todo.completed_on && (
              <Button
                className="boxedButton "
                onClick={() => {
                  completeHandler(todo.id);
                }}
              >
                <Icon src="Tick" />
              </Button>
            )}
            {!todo.completed_on && !todo.isEdit && (
              <Button
                onClick={() => editToggle(todo.id)}
                className="boxedButton "
              >
                <Icon src="Edit" />
              </Button>
            )}
            <Button className="boxedButton" onClick={() => onDelete(todo.id)}>
              <Icon src="Delete" />
            </Button>
          </div>
          {todo.completed_on && (
            <Tag className="completedOn">
              completed in{" "}
              {Math.ceil(
                Math.abs(
                  Date.parse(todo.completed_on) - Date.parse(todo.created_at),
                ) /
                  (1000 * 60 * 60 * 24),
              )}{" "}
              days
            </Tag>
          )}
        </div>
      </div>
      {todo.isLoading && <Icon className="spinning rotateDiv" src="Spin" />}
    </div>
  );
};

export default Todo;
