/* eslint-disable react-hooks/rules-of-hooks */
import React, { useState } from "react";
import Todo from "./Todo";
import { TextArea } from "./components/TextArea";
import { Icon } from "./components/Icon";
import { Button } from "./components/Button";
import { v4 as uuidv4 } from "uuid";
const todos = ({
  todos,

  flagHandler,

  toasts,
  setToasts,
}) => {
  const [top, setTop] = useState(12);
  const [showSpinner, setShowSpinner] = useState(false);
  const [taskvalue, setTaskvalue] = useState("");
  const [showFullSpinner, setShowFullSpinner] = useState(false);
  const [show, setShow] = useState(false);
  // addhandler
  //add task value
  const task = (e) => {
    setTaskvalue(e);
  };
  const toggleHandler = (e) => {
    e.preventDefault();
    setShow(!show);
  };
  const addhandler = (e) => {
    if (taskvalue.length < 3) {
      let newToast = {
        id: uuidv4(),
        type: "error",
        message: "Task must be more then 3 character",
      };
      let allToast = [...toasts];
      allToast.push(newToast);
      setToasts(allToast);
    } else {
      setShowSpinner(true);
      setTimeout(() => {
        let newTodo = {
          id: uuidv4(),
          name: taskvalue,
          created_at: new Date(Date.now()).toLocaleDateString(),
          completed_on: null,
        };
        let tempTodos = [newTodo, ...todos];

        setTodos(tempTodos);
        setShow(!show);
        setShowSpinner(false);
        let newToast = {
          id: uuidv4(),
          type: "success",
          message: "New Task added",
        };
        let allToast = [...toasts];
        allToast.push(newToast);
        setToasts(allToast);
      }, 500);
    }
  };
  const moreValue = (e) => {
    setShowFullSpinner(true);
    setTimeout(() => {
      setTop(top + 12);

      setShowFullSpinner(false);
    }, 300);
  };
  const showless = (e) => {
    setTop(12);
  };
  return (
    <div>
      <div className={`${showFullSpinner && "blur"}`}>
        <div className="addTask">
          <h1 className="addTaskH1">Add Tasks</h1>
        </div>

        <div className="headerClass">
          <Button className="create btn" onClick={toggleHandler}>
            <Icon src="Plus" />
            Create
          </Button>
          <div className={`topButtonAll ${todos.length === 0 && "blurButton"}`}>
            <Button
              className={`topButton btn `}
              disabled={todos.length === 0}
              onClick={(e) => {
                flagHandler("all");
              }}
            >
              All
            </Button>
            <Button
              className={`topButton btn `}
              disabled={todos.length === 0}
              onClick={(e) => {
                flagHandler("incomplete");
              }}
            >
              Incomplete
            </Button>
            <Button
              className={`topButton btn `}
              disabled={todos.length === 0}
              onClick={(e) => {
                flagHandler("complete");
              }}
            >
              Complete
            </Button>
          </div>
        </div>
        <div className="todos">
          {show && (
            <div id="todo" className="todo">
              <div>
                <TextArea
                  className={`editName ${showSpinner && "blur"}`}
                  placeholder="Add new task"
                  autoFocus
                  onChange={(e) => task(e.target.value)}
                  readOnly={showSpinner}
                  onKeyPress={(e) => {
                    if (e.key === "Enter") {
                      e.preventDefault();
                      addhandler(e);
                    }
                  }}
                  onFocus={function (e) {
                    var val = e.target.value;
                    e.target.value = "";
                    e.target.value = val;
                  }}
                />
              </div>
              <div className="addDel">
                <Button
                  className={`saveButton btn ${showSpinner && "blur"}`}
                  onClick={addhandler}
                >
                  Add Task
                </Button>
                <Button className="boxedButton" onClick={toggleHandler}>
                  <Icon src="Delete" />
                </Button>
              </div>

              {showSpinner && (
                <div>
                  <Icon className="spinning rotateDiv" src="Spin" />
                </div>
              )}
            </div>
          )}
          {todos.slice(0, top).map((todo) => (
            <Todo
              allTodos={todos}
              todo={todo}
              key={todo.id}
              setTodos={setTodos}
              toasts={toasts}
              setToasts={setToasts}
            />
          ))}
        </div>

        {!showFullSpinner && (
          <div className="bottomButton">
            {todos.length <= 12 ? null : top <= todos.length ? (
              <div className="loadmoreOver">
                <Button className="loadMoreBtnContainer" onClick={moreValue}>
                  Load More
                </Button>
              </div>
            ) : (
              <div className="loadmoreOver">
                <Button className="loadMoreBtnContainer" onClick={showless}>
                  Show Less
                </Button>
              </div>
            )}
          </div>
        )}
      </div>
      <div>
        {showFullSpinner && <Icon className="spinning rotateFull" src="Spin" />}
      </div>
    </div>
  );
};

export default todos;
