import React from "react";
import Todo from "./Todo";
import { Button, TextArea, Icon } from "./App";
const todos = ({
  todos,
  onDelete,
  completeHandler,
  show,
  togglehandler,
  addhandler,
  task,
  showSpinner,
  showBigSpinner,
  flagHandler,
  todoLength,
  editToggle,
  setTodos,
  allTodos,
}) => {
  return (
    <div className={`${showBigSpinner && "blur"}`}>
      <div className="addTask">
        <h1 className="addTaskH1">Add Tasks</h1>
      </div>

      <div className="headerClass">
        <Button className="create btn" onClick={togglehandler}>
          <Icon src="Plus" />
          Create
        </Button>
        <div>
          <Button
            className={`topButton btn ${todoLength === 0 && "blurButton"}`}
            disabled={todoLength === 0}
            onClick={(e) => {
              flagHandler("all");
            }}
          >
            All
          </Button>
          <Button
            className={`topButton btn ${todoLength === 0 && "blurButton"}`}
            disabled={todoLength === 0}
            onClick={(e) => {
              flagHandler("incomplete");
            }}
          >
            Incomplete
          </Button>
          <Button
            className={`topButton btn ${todoLength === 0 && "blurButton"}`}
            disabled={todoLength === 0}
            onClick={(e) => {
              flagHandler("complete");
            }}
          >
            Complete
          </Button>
        </div>
      </div>
      <div className="todos">
        {show && (
          <div id="todo" className="todo">
            <div className={`${showSpinner && "blur"}`}>
              <TextArea
                className="editName"
                placeholder="Add new task"
                autoFocus
                onChange={(e) => task(e.target.value)}
                readOnly={showSpinner}
                onKeyPress={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addhandler(e);
                  }
                }}
                onFocus={function (e) {
                  var val = e.target.value;
                  e.target.value = "";
                  e.target.value = val;
                }}
              />
              <Button className="saveButton btn" onClick={addhandler}>
                Add Task
              </Button>
            </div>
            <div>
              {showSpinner && (
                <Icon className="spinning rotateDiv" src="Spin" />
              )}
            </div>
          </div>
        )}
        {todos.map((todo) => (
          <Todo
            allTodos={todos}
            todo={todo}
            key={todo.id}
            onDelete={onDelete}
            completeHandler={completeHandler}
            editToggle={editToggle}
            setTodos={setTodos}
          />
        ))}
      </div>
    </div>
  );
};

export default todos;
