import React from "react";
import spin from "./images/bigspin.svg";
import Edit from "./images/edit.svg";
import tick from "./images/tick.svg";
import deleteSvg from "./images/Vector.svg";
const Todo = ({ todo, onDelete, completeHandler, editToggle }) => {
  return (
    <div id="todo" className="todo">
      <div className={`${todo.isLoading && "blur"}`}>
        {todo.isEdit === true ? (
          <textarea
            id="editName"
            className="editName"
            value={todo.name}
            /* onChange={edit} */
            autoFocus
            onFocus={(e) => {
              var val = e.target.value;
              e.target.value = "";
              e.target.value = val;
            }}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                e.preventDefault();
                editToggle(todo.id);
              }
            }}
          />
        ) : (
          <label
            id="showName"
            key={todo.id}
            className={`${todo.completed_on && "completed"}`}
          >
            {todo.name}
          </label>
        )}
        <div className="createdAt">
          <div>
            <label>created_at: {todo.created_at}</label>
          </div>
        </div>
        <div className="boxedButtonCompletedOn">
          <div>
            {todo.isEdit && (
              <button
                className="saveButton btn"
                onClick={() => editToggle(todo.id)}
              >
                Save
              </button>
            )}
            {!todo.completed_on && (
              <button className="boxedButton ">
                <img
                  src={tick}
                  alt="tick"
                  onClick={() => {
                    completeHandler(todo.id);
                  }}
                />
              </button>
            )}
            {!todo.completed_on && !todo.isEdit && (
              <button
                onClick={() => editToggle(todo.id)}
                className="boxedButton "
              >
                <img src={Edit} alt="Edit" />
              </button>
            )}
            <button className="boxedButton" onClick={() => onDelete(todo.id)}>
              <img src={deleteSvg} alt="Delete" />
            </button>
          </div>
          {todo.completed_on && (
            <label className="completedOn">
              completed in{" "}
              {Math.ceil(
                Math.abs(
                  Date.parse(todo.completed_on) - Date.parse(todo.created_at),
                ) /
                  (1000 * 60 * 60 * 24),
              )}{" "}
              days
            </label>
          )}
        </div>
      </div>
      {todo.isLoading && (
        <img className="spinning rotateDiv" src={spin} alt="spin" />
      )}
    </div>
  );
};

export default Todo;
