    allToast.push(newToast);
    setToasts(allToast);
    setTimeout(() => {
      setTodos(todos.filter((todo) => todo.isLoading !== true));
    }, 500);
  };
  //edit toggle
  const editToggle = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isEdit = !tempTodos[index].isEdit;
