    }
  };

  // on Complete

  const completeHandler = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isLoading = true;
    setTodos(tempTodos);
    setTimeout(() => {
      tempTodos = [...todos];
      tempTodos[index].completed_on = new Date(Date.now()).toLocaleDateString();
      tempTodos[index].isLoading = false;
      setTodos(tempTodos);
    }, 1000);
  };
  //search value
  const searchvalue = (e) => {
    if (e.length > 2 || e.length === 0) {
      setTimeout(() => {
        setShowBigSpinner(true);
      }, 500);
      setTimeout(() => {
        setSearch(e);
        setShowBigSpinner(false);
      }, 1500);
    }
  };
  //add task value
  const task = (e) => {
    setTaskvalue(e);
  };
  // addhandler
  const addhandler = (e) => {
    if (taskvalue.length < 3) window.alert("less then 3");
    else {
      setShowSpinner(true);
      setTimeout(() => {
        let newTodo = {
          id: uuidv4(),
          name: taskvalue,
          created_at: new Date(Date.now()).toLocaleDateString(),
          completed_on: null,
          isLoading: false,
          isEdit: false,
        };
        let tempTodos = [newTodo, ...todos];

        setTodos(tempTodos);
        togglehandler();
        setShowSpinner(false);
      }, 500);
    }
  };
  //toggle
  const togglehandler = (e) => {
    setTaskvalue("");
    setShow(!show);
  };
  //search toggle
  const SearchToggle = () => {
    if (search !== "") {
      setShowBigSpinner(true);
      setTimeout(() => {
        setSearch("");
        setShowBigSpinner(false);
      }, 500);
    }
    setSearchShow(!searchShow);
  };
  /*  let newToast = {
    id: 3,
    type: "success",
  };
  let allToast = [...toasts];
  allToast.push(newToast);
  setToasts(allToast); */
  // useEffect
  useEffect(() => {
    setTodoLength(todos.length);
    const arr = [];
    if (flag === "all") {
      todos.map((todo) => {
        if (todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase()))
          arr.push(todo);
        // setShowTodos(arr);
      });
    } else if (flag === "incomplete") {
      todos.map((todo) => {
        if (
          todo.completed_on === null &&
          todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
        )
          arr.push(todo);
        // setShowTodos(arr);
      });
    } else {
      todos.map((todo) => {
        if (
          todo.completed_on !== null &&
          todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
        )
          arr.push(todo);
      });
    }
    if (todos.length === 0) setFlag("all");
    setShowTodos(arr);
  }, [flag, todos, search, toasts]);
  //Toast useeffect
  useEffect(() => {
    if (toasts.length > 0) {
      setTimeout(() => {
        let allToast = [...toasts];
        allToast.shift();
        setToasts(allToast);
      }, 1000);
    }
  }, [toasts]);
  return (
    <div className="App">
      <Header
        SearchToggle={SearchToggle}
        searchShow={searchShow}
        searchvalue={searchvalue}
        showBigSpinner={showBigSpinner}
        todoLength={todoLength}
      />

      {toasts.length > 0 &&
        toasts.map((toast) => {
          <Toaster
            key={toasts[0].id}
            className={`faddingOut toast toast--visible toast--${toasts[0].type}`}
          >
            Toaster
          </Toaster>;
        })}

      <Todos
        todos={showTodos}
        allTodos={todos}
        onDelete={deletetodo}
        completeHandler={completeHandler}
        show={show}
        togglehandler={togglehandler}
        addhandler={addhandler}
        task={task}
        showSpinner={showSpinner}
        showBigSpinner={showBigSpinner}
        todoLength={todoLength}
        flagHandler={flagHandler}
        editToggle={editToggle}
        setTodos={setTodos}
      />
      <div>
        {showBigSpinner && <Icon className="spinning rotateFull" src="Spin" />}
      </div>
      {todoLength === 0 && !show && (
        <div>
          <Icon src="EmptyScreen" className="emptyScreen" />
          <Tag className="pleaseAdd">
            You didn't add any task. Please, add one.
          </Tag>
        </div>
      )}
      {todoLength !== 0 && showTodos.length === 0 && !show && (
        <div>
          <Icon src="EmptyScreen" className="emptyScreen" />
          <Tag className="pleaseAdd"> There is no data for {flag}</Tag>
        </div>
      )}
    </div>
  );
}

export default App;
