/* eslint-disable array-callback-return */

import "./App.css";

import { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "./Header";
import { Icon } from "./components/Icon";
import { Tag } from "./components/Tag";
import { Toaster } from "./components/Toaster";
import { supabase } from "./client";

function App() {
  const [todos, setTodos] = useState([]);

  const [toasts, setToasts] = useState([]);
  // eslint-disable-next-line
  const [show, setShow] = useState(false);
  const [showBigSpinner, setShowBigSpinner] = useState(false);
  const [searchShow, setSearchShow] = useState(false);
  const [flag, setFlag] = useState("all");
  const [search, setSearch] = useState("");
  const [dataCount, setDataCount] = useState(0);
  // const [todoLength, setTodoLength] = useState(0);
  // const [splash, setSplash] = useState(true);

  //flag
  const flagHandler = (e) => {
    if (flag !== e) {
      setFlag(e);
    }
  };

  //search value
  const searchvalue = (e) => {
    if (e.length > 2 /*  || e.length === 0 */) {
      setTimeout(() => {
        setShowBigSpinner(true);
      }, 500);
      setTimeout(() => {
        setSearch(e);
        setShowBigSpinner(false);
      }, 1500);
    }
  };

  //search toggle
  const SearchToggle = () => {
    if (search !== "") {
      setShowBigSpinner(true);
      setTimeout(() => {
        setSearch("");
        setShowBigSpinner(false);
      }, 500);
    }
    setSearchShow(!searchShow);
  };
  //splash useEffect
  /*  useEffect(() => {
    setTimeout(() => {
      setSplash(false);
    }, 1000);
  }, []); */
  // useEffect
  useEffect(() => {
    const fetchData = async (e) => {
      setShowBigSpinner(true);
      if (flag === "all") {
        const { data } = await supabase
          .from("ReactTodo")
          .select()
          .ilike("name", `%${search}%`)
          .order("id", { ascending: false });
        setTodos([]);
        setDataCount(data.length);
        setTodos(data);
      } else if (flag === "incomplete") {
        const { data } = await supabase
          .from("ReactTodo")
          .select()
          .ilike("name", `%${search}%`)
          .is("completed_on", null)
          .order("id", { ascending: false });

        setTodos([]);
        setTodos(data);
      } else {
        const { data } = await supabase
          .from("ReactTodo")
          .select()
          .ilike("name", `%${search}%`)
          .order("id", { ascending: false })
          .not("completed_on", "is", null);

        setTodos([]);
        setTodos(data);
      }
      /*  if (todos === null || todos.length === undefined) {
        setFlag("all");
        setSearchShow(false);
      } */
      // console.log(todos.length);
      setShowBigSpinner(false);
    };
    fetchData();
  }, [flag, search]);
  //Toast useeffect
  /*  useEffect(() => {
    if (toasts.length > 0) {
      setTimeout(() => {
        let allToast = [...toasts];
        allToast.shift();
        setToasts(allToast);
      }, 500);
    }
  }, [toasts]); */
  const handleRemoveTodo = (id) => {
    const filtertodos = todos.filter((todo) => todo.id !== id);
    setTodos(filtertodos);
  };
  return (
    <div className="App">
      {/* {splash && (
        <div className="splash faddingOut">
          <div className="splashLogo">
            <div className="splashIconLogo">
              <Icon src="SplashLeaf" />
            </div>
            <div>
              <Icon src="SplashTitle" />
            </div>
          </div>
        </div>
      )} */}
      {
        /* !splash && */ <div>
          <Header
            SearchToggle={SearchToggle}
            searchShow={searchShow}
            searchvalue={searchvalue}
          />
          <div className="overToaster">
            <div className="toaster">
              {toasts.length > 0 &&
                toasts.map((toast) => (
                  <Toaster
                    key={toast.id}
                    className={`faddingOut toast toast--visible toast--${toast.type}`}
                  >
                    {toast.type === "success" && <Icon src="Check" />}{" "}
                    {toast.message}
                  </Toaster>
                ))}
            </div>
          </div>
          <div className={`${showBigSpinner && "blur"}`}>
            <Todos
              todos={todos}
              flag={flag}
              flagHandler={flagHandler}
              setTodos={setTodos}
              toasts={toasts}
              onRemoveTodo={handleRemoveTodo}
              dataCount={dataCount}
            />
          </div>

          <div>
            {showBigSpinner && (
              <Icon className="spinning rotateFull" src="Spin" />
            )}
          </div>

          {dataCount === 0 && !show && (
            <div className={`emptyScreenOver ${showBigSpinner && "blur"}`}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd">
                You didn't add any task. Please, add one.
              </Tag>
            </div>
          )}
          {dataCount !== 0 && todos.length === 0 && !show && (
            <div className={`emptyScreenOver ${showBigSpinner && "blur"}`}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd"> There is no data for {flag}</Tag>
            </div>
          )}
        </div>
      }
    </div>
  );
}

export default App;
