import "./App.css";
import { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "./Header";
function App() {
  const [todos, setTodos] = useState([
    {
      id: 1,
      name: "Hello",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
    },
    {
      id: 2,
      name: "Hola",
      created_at: "2022-03-31",
      completed_on: null,
      isLoading: false,
    },
    {
      id: 3,
      name: "dshdksgh",
      created_at: "2022-04-07",
      completed_on: "2022-04-07",
      isLoading: false,
    },
    {
      id: 4,
      name: "Running",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
    },
  ]);
  const [show, setShow] = useState(false);
  const [taskvalue, setTaskvalue] = useState("");
  // Delete task
  const deletetodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };
  // on Complete

  const completeHandler = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isLoading = true;
    setTodos((prev) => tempTodos);
    setTimeout(() => {
      const d = new Date(Date.now());
      tempTodos = [...todos];
      tempTodos[
        index
      ].completed_on = `${d.getFullYear()} - ${d.getMonth()} - ${d.getDate()}`;
      tempTodos[index].isLoading = false;
      setTodos((prev) => tempTodos);
    }, 1000);
  };
  // addhandler
  const addhandler = (e) => {
    console.log(e.target);
  };
  //toggle
  const togglehandler = (e) => {
    e.preventDefault();
    setShow(!show);
  };
  /* useEffect(() => {
    setTodos(todos);
  }, [todos]); */
  return (
    <div className="App">
      <Header />
      <Todos
        todos={todos}
        onDelete={deletetodo}
        completeHandler={completeHandler}
        show={show}
        togglehandler={togglehandler}
        addhandler={addhandler}
      />
    </div>
  );
}

export default App;
