import "./App.css";
import { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "./Header";
import { v4 as uuidv4 } from "uuid";
function App() {
  const [todos, setTodos] = useState([
    {
      id: 1,
      name: "Hello",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
      isEdit: false,
    },
    {
      id: 2,
      name: "Hola",
      created_at: "2022-03-31",
      completed_on: null,
      isLoading: false,
      isEdit: false,
    },
    {
      id: 3,
      name: "dshdksgh",
      created_at: "2022-04-07",
      completed_on: "2022-04-07",
      isLoading: false,
      isEdit: false,
    },
    {
      id: 4,
      name: "Running",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
      isEdit: false,
    },
  ]);
  const [show, setShow] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [showBigSpinner, setShowBigSpinner] = useState(false);
  const [taskvalue, setTaskvalue] = useState("");
  const [searchShow, setSearchShow] = useState(false);
  const [flag, setFlag] = useState("all");
  const [showTodos, setShowTodos] = useState(todos);
  // Delete task
  const deletetodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  //flag
  const flagHandler = (e) => {
    if (flag !== e) {
      setShowBigSpinner(true);
      setTimeout(() => {
        setFlag(e);
        setShowBigSpinner(false);
      }, 1000);
      /* setFlag(e); */
    }
  };

  // on Complete

  const completeHandler = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isLoading = true;
    setTodos(tempTodos);
    setTimeout(() => {
      tempTodos = [...todos];
      tempTodos[index].completed_on = new Date(Date.now()).toLocaleDateString();
      tempTodos[index].isLoading = false;
      setTodos(tempTodos);
    }, 1000);
  };

  // addhandler
  const addhandler = (e) => {
    if (taskvalue.length < 3) window.alert("less then 3");
    else {
      setShowSpinner(true);
      setTimeout(() => {
        let newTodo = {
          id: uuidv4(),
          name: taskvalue,
          created_at: new Date(Date.now()).toLocaleDateString(),
          completed_on: null,
          isLoading: false,
          isEdit: false,
        };
        let tempTodos = [newTodo, ...todos];

        setTodos(tempTodos);
        togglehandler();
        setShowSpinner(false);
      }, 500);
    }
  };
  //toggle
  const togglehandler = (e) => {
    setTaskvalue("");
    setShow(!show);
  };
  //search toggle
  const SearchToggle = () => {
    setTaskvalue("");
    setSearchShow(!searchShow);
  };

  useEffect(() => {
    // setTodos(todos);

    if (flag === "all") setShowTodos(todos);
    else if (flag === "incomplete") {
      const arr = [];
      todos.map((todo) => {
        if (todo.completed_on === null) arr.push(todo);
        setShowTodos(arr);
      }, {});
    } else {
      const arr = [];
      todos.map((todo) => {
        if (todo.completed_on !== null) arr.push(todo);
        setShowTodos(arr);
      });
    }
  }, [flag, todos]);
  return (
    <div className="App">
      <Header SearchToggle={SearchToggle} searchShow={searchShow} />
      <Todos
        todos={showTodos}
        onDelete={deletetodo}
        completeHandler={completeHandler}
        show={show}
        togglehandler={togglehandler}
        addhandler={addhandler}
        setTaskvalue={setTaskvalue}
        showSpinner={showSpinner}
        showBigSpinner={showBigSpinner}
        flagHandler={flagHandler}
      />
    </div>
  );
}

export default App;
