import React from "react";
import spin from "./images/bigspin.svg";
import Edit from "./images/edit.svg";
// import spin from "./images/bigspin.svg";
import tick from "./images/tick.svg";
import deleteSvg from "./images/Vector.svg";
const Todo = ({ todo, onDelete, completeHandler, showSpinner }) => {
  return (
    <div id="todo" className="todo">
      <label id="showName" key={todo.id}>
        {todo.name}
      </label>
      <div className="createdAt">
        <div>
          <label>created_at: {todo.created_at}</label>
        </div>
      </div>
      <div className="boxedButtonCompletedOn">
        <div>
          {!todo.completed_on && (
            <div>
              <button className="boxedButton ">
                <img
                  src={tick}
                  alt="tick"
                  onClick={() => completeHandler(todo.id)}
                />
              </button>
              <button /* onClick={togglehandler} */ className="boxedButton ">
                <img src={Edit} alt="Edit" />
              </button>
            </div>
          )}
          <button className="boxedButton" onClick={() => onDelete(todo.id)}>
            <img src={deleteSvg} alt="Delete" />
          </button>
        </div>
        {todo.completed_on && (
          <label className="completedOn">
            completed_on: {todo.completed_on}
          </label>
        )}
      </div>
      {showSpinner && (
        <img className="spinning rotateDiv" src={spin} alt="spin" />
      )}
    </div>
  );
};

export default Todo;
