import React, { useState } from "react";
import App from "../App";

export const Context = React.createContext();
function AppContext() {
  return (
    <div>
      <Context.Provider
        value={{
          toasts,
          setToasts,
          todos,
          setTodos,
          showEmpty,
          setShowEmpty,
          showBigSpinner,
          setShowBigSpinner,
          searchShow,
          setSearchShow,
          flag,
          setFlag,
          search,
          setSearch,
          dataCount,
          setDataCount,
          splash,
          setSplash,
        }}
      >
        <App />
      </Context.Provider>
    </div>
  );
}

export default AppContext;
