import { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { TextArea } from "./components/TextArea";
import { Icon } from "./components/Icon";
import { Tag } from "./components/Tag";
import { Button } from "./components/Button";
import { format } from "date-fns";
const Todo = ({
  todo,

  setTodos,
  onRemoveItem,
  toasts,
  setToasts,
}) => {
  const [newName, setNewName] = useState(todo.name);
  const [showEdit, setShowEdit] = useState(false);
  const [showLoading, setShowLoading] = useState(false);
  const editToggle = (e) => {
    setShowEdit(!showEdit);
  };
  const edit = (e) => {
    setNewName(e.target.value);
  };
  // Delete task
  const deletetodo = (id) => {
    setShowLoading(true);

    setTimeout(() => {
      onRemoveItem(id);
      // toaster
      let newToast = {
        id: uuidv4(),
        type: "success",
        message: "Task Deleted",
      };
      let allToast = [...toasts];
      allToast.push(newToast);
      setToasts(allToast);
    }, 500);
  };
  // on Complete

  const completeHandler = (id) => {
    const index = allTodos.findIndex((todo) => todo.id === id);
    let tempTodos = [...allTodos];
    tempTodos[index].isLoading = true;
    setTodos(tempTodos);
    setTimeout(() => {
      tempTodos = [...allTodos];
      tempTodos[index].completed_on = new Date(Date.now()).toLocaleDateString();
      tempTodos[index].isLoading = false;
      setTodos(tempTodos);
      let newToast = {
        id: uuidv4(),
        type: "success",
        message: "Task Complted",
      };
      let allToast = [...toasts];
      allToast.push(newToast);
      setToasts(allToast);
    }, 1000);
  };

  const editValue = (id) => {
    setNewName(newName.trim().replace(/\s+/g, " "));
    const index = allTodos.findIndex((todo) => todo.id === id);
    if (
      newName.trim().replace(/\s+/g, " ").length > 2 &&
      allTodos[index].name !== newName.trim().replace(/\s+/g, " ")
    ) {
      setShowLoading(true);
      setTimeout(() => {
        let tempTodos = [...allTodos];
        tempTodos[index].name = newName;
        setTodos(tempTodos);

        editToggle(id);
        setShowLoading(false);
        let newToast = {
          id: uuidv4(),
          type: "success",
          message: "Task Edited",
        };
        let allToast = [...toasts];
        allToast.push(newToast);
        setToasts(allToast);
      }, 1000);
    } else {
      setNewName(allTodos[index].name);
      editToggle(id);
    }
  };

  return (
    <div id="todo" className="todo">
      <div>
        {showEdit === true ? (
          <TextArea
            id="editName"
            className={`editName ${(todo.isLoading || showLoading) && "blur"}`}
            value={newName}
            onChange={edit}
            readOnly={showLoading}
            autoFocus
            onFocus={(e) => {
              var val = e.target.value;
              e.target.value = "";
              e.target.value = val;
            }}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                e.preventDefault();
                editValue(todo.id);
              }
            }}
          />
        ) : (
          <div>
            <Tag
              id="showName"
              className={`${todo.completed_on && "completed"} ${
                (todo.isLoading || showLoading) && "blur"
              }`}
            >
              {todo.name}
            </Tag>
          </div>
        )}
        {!showEdit && (
          <div
            className={`createdAt ${(todo.isLoading || showLoading) && "blur"}`}
          >
            Created At: {format(new Date(todo.created_at), "dd.MM.yy")}
          </div>
        )}
      </div>
      <div
        className={`boxedButtonCompletedOn ${
          (todo.isLoading || showLoading) && "blur"
        }`}
      >
        <div className="allBoxedButon">
          {showEdit && (
            <Button className="saveButton btn" onClick={editToggle}>
              Save
            </Button>
          )}
          {!todo.completed_on && (
            <Button
              className="boxedButton "
              onClick={() => {
                completeHandler(todo.id);
              }}
            >
              <Icon src="Tick" />
            </Button>
          )}
          {!todo.completed_on && !showEdit && (
            <Button onClick={editToggle} className="boxedButton ">
              <Icon src="Edit" />
            </Button>
          )}
          <Button className="boxedButton" onClick={() => deletetodo(todo.id)}>
            <Icon src="Delete" />
          </Button>
        </div>
        {todo.completed_on && (
          <Tag className="completedOn">
            Completed in{" "}
            {Math.ceil(
              Math.abs(
                Date.parse(todo.completed_on) - Date.parse(todo.created_at),
              ) /
                (1000 * 60 * 60 * 24),
            )}{" "}
            days
          </Tag>
        )}
      </div>

      {(todo.isLoading || showLoading) && (
        <Icon className="spinning rotateDiv" src="Spin" />
      )}
    </div>
  );
};

export default Todo;
