import "./App.css";
import { useState } from "react";
import Tasks from "./Todos";
function App() {
  const [todos, setTodos] = useState([
    {
      id: 1,
      name: "Hello",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
    },
    {
      id: 2,
      name: "Hola",
      created_at: "2022-03-31",
      completed_on: null,
    },
    {
      id: 3,
      name: "dshdksgh",
      created_at: "2022-04-07",
      completed_on: "2022-04-07",
    },
  ]);

  // Delete task
  const deletetodo = (id) => {
    setTodos(tasks.filter((todo) => todo.id !== id));
  };

  return (
    <div className="App">
      <Todos todos={todos} onDelete={deletetodo} />
    </div>
  );
}

export default App;
