/* eslint-disable array-callback-return */

import "./App.css";

import { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "./Header";
import { Icon } from "./components/Icon";
import { Tag } from "./components/Tag";
import { v4 as uuidv4 } from "uuid";
import { Toaster } from "./components/Toaster";

function App() {
  const [todos, setTodos] = useState([]);

  const [toasts, setToasts] = useState([]);
  const [show, setShow] = useState(false);

  const [showBigSpinner, setShowBigSpinner] = useState(false);

  const [searchShow, setSearchShow] = useState(false);
  const [flag, setFlag] = useState("all");
  const [showTodos, setShowTodos] = useState(todos);
  const [search, setSearch] = useState("");
  const [todoLength, setTodoLength] = useState(0);
  const [splash, setSplash] = useState(true);

  //flag
  const flagHandler = (e) => {
    if (flag !== e) {
      setShowBigSpinner(true);
      setTimeout(() => {
        setFlag(e);
        setShowBigSpinner(false);
      }, 1000);
    }
  };

  // on Complete

  const completeHandler = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    // if (tempTodos[index].isEdit) editToggle(id);
    tempTodos[index].isLoading = true;
    setTodos(tempTodos);
    setTimeout(() => {
      tempTodos = [...todos];
      tempTodos[index].completed_on = new Date(Date.now()).toLocaleDateString();
      tempTodos[index].isLoading = false;
      setTodos(tempTodos);
      let newToast = {
        id: uuidv4(),
        type: "success",
        message: "Task Complted",
      };
      let allToast = [...toasts];
      allToast.push(newToast);
      setToasts(allToast);
    }, 1000);
  };
  //search value
  const searchvalue = (e) => {
    if (e.length > 2 /*  || e.length === 0 */) {
      setTimeout(() => {
        setShowBigSpinner(true);
      }, 500);
      setTimeout(() => {
        setSearch(e);
        setShowBigSpinner(false);
      }, 1500);
    }
  };
  //add task value
  const task = (e) => {
    setTaskvalue(e);
  };

  //toggle
  const togglehandler = (e) => {
    setTaskvalue("");
    setShow(!show);
  };
  //search toggle
  const SearchToggle = () => {
    if (search !== "") {
      setShowBigSpinner(true);
      setTimeout(() => {
        setSearch("");
        setShowBigSpinner(false);
      }, 500);
    }
    setSearchShow(!searchShow);
  };
  //splash useEffect
  useEffect(() => {
    setTimeout(() => {
      setSplash(false);
    }, 1000);
  }, []);
  // useEffect
  useEffect(() => {
    setTodoLength(todos.length);

    const arr = [];
    if (flag === "all") {
      todos.map((todo) => {
        if (todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase()))
          arr.push(todo);
        // setShowTodos(arr);
      });
    } else if (flag === "incomplete") {
      todos.map((todo) => {
        if (
          todo.completed_on === null &&
          todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
        )
          arr.push(todo);
        // setShowTodos(arr);
      });
    } else {
      todos.map((todo) => {
        if (
          todo.completed_on !== null &&
          todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
        )
          arr.push(todo);
      });
    }
    if (todos.length === 0) {
      setFlag("all");
      setSearchShow(false);
    }

    setShowTodos(arr);
  }, [flag, todos, search, toasts]);
  //Toast useeffect
  useEffect(() => {
    if (toasts.length > 0) {
      setTimeout(() => {
        let allToast = [...toasts];
        allToast.shift();
        setToasts(allToast);
      }, 500);
    }
  }, [toasts]);
  return (
    <div className="App">
      {splash && (
        <div className="splash faddingOut">
          <div className="splashLogo">
            <div className="splashIconLogo">
              <Icon src="SplashLeaf" />
            </div>
            <div>
              <Icon src="SplashTitle" />
            </div>
          </div>
        </div>
      )}
      {!splash && (
        <div>
          <Header
            SearchToggle={SearchToggle}
            searchShow={searchShow}
            searchvalue={searchvalue}
            showBigSpinner={showBigSpinner}
            todoLength={todoLength}
          />
          <div className="overToaster">
            <div className="toaster">
              {toasts.length > 0 &&
                toasts.map((toast) => (
                  <Toaster
                    key={toast.id}
                    className={`faddingOut toast toast--visible toast--${toast.type}`}
                  >
                    {toast.type === "success" && <Icon src="Check" />}{" "}
                    {toast.message}
                  </Toaster>
                ))}
            </div>
          </div>
          <Todos
            todos={showTodos}
            allTodos={todos}
            completeHandler={completeHandler}
            show={show}
            togglehandler={togglehandler}
            task={task}
            showSpinner={showSpinner}
            showBigSpinner={showBigSpinner}
            todoLength={todoLength}
            flagHandler={flagHandler}
            setTodos={setTodos}
            toasts={toasts}
            setToasts={setToasts}
          />

          <div>
            {showBigSpinner && (
              <Icon className="spinning rotateFull" src="Spin" />
            )}
          </div>
          {todoLength === 0 && !show && (
            <div className={`emptyScreenOver ${showBigSpinner && "blur"}`}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd">
                You didn't add any task. Please, add one.
              </Tag>
            </div>
          )}
          {todoLength !== 0 && showTodos.length === 0 && !show && (
            <div className={`emptyScreenOver ${showBigSpinner && "blur"}`}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd"> There is no data for {flag}</Tag>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
