/* eslint-disable array-callback-return */
import "./App.css";
import { useState, useEffect } from "react";
import Todos from "./Todos";
import spin from "./images/bigspin.svg";
import Header from "./Header";
import emptyScreen from "./images/undraw_add_files_re_v09g 1.svg";
import { v4 as uuidv4 } from "uuid";
function App() {
  const [todos, setTodos] = useState([
    {
      id: 1,
      name: "Hello",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
      isEdit: false,
    },
    {
      id: 2,
      name: "Hola",
      created_at: "2022-03-31",
      completed_on: null,
      isLoading: false,
      isEdit: false,
    },
    {
      id: 3,
      name: "dshdksgh",
      created_at: "2022-04-07",
      completed_on: "2022-04-07",
      isLoading: false,
      isEdit: false,
    },
    {
      id: 4,
      name: "Running",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
      isEdit: false,
    },
  ]);
  const [show, setShow] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [showBigSpinner, setShowBigSpinner] = useState(false);
  const [taskvalue, setTaskvalue] = useState("");
  const [searchShow, setSearchShow] = useState(false);
  const [flag, setFlag] = useState("all");
  const [showTodos, setShowTodos] = useState(todos);
  const [search, setSearch] = useState("");
  const [todoLength, setTodoLength] = useState(0);

  // Delete task
  const deletetodo = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isLoading = true;
    setTodos(tempTodos);
    tempTodos = [...todos];
    tempTodos.splice(index, 1);
    // setTodos(todos.filter((todo) => todo.id !== id));
    setTimeout(() => {
      setTodos(tempTodos);
      // setShowTodos(todos.filter((todo) => todo.id !== id));
    }, 1000);
  };
  //edit toggle
  const editToggle = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isEdit = !tempTodos[index].isEdit;
    setTodos(tempTodos);
  };
  //flag
  const flagHandler = (e) => {
    if (flag !== e) {
      setShowBigSpinner(true);
      setTimeout(() => {
        setFlag(e);
        setShowBigSpinner(false);
      }, 1000);
    }
  };

  // on Complete

  const completeHandler = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isLoading = true;
    setTodos(tempTodos);
    setTimeout(() => {
      tempTodos = [...todos];
      tempTodos[index].completed_on = new Date(Date.now()).toLocaleDateString();
      tempTodos[index].isLoading = false;
      setTodos(tempTodos);
    }, 1000);
  };
  //search value
  const searchvalue = (e) => {
    if (e.length > 2) {
      setTimeout(() => {
        setShowBigSpinner(true);
      }, 500);
      setTimeout(() => {
        setSearch(e);
        setShowBigSpinner(false);
      }, 1500);
    }
  };
  //add task value
  const task = (e) => {
    setTaskvalue(e);
  };
  // addhandler
  const addhandler = (e) => {
    if (taskvalue.length < 3) window.alert("less then 3");
    else {
      setShowSpinner(true);
      setTimeout(() => {
        let newTodo = {
          id: uuidv4(),
          name: taskvalue,
          created_at: new Date(Date.now()).toLocaleDateString(),
          completed_on: null,
          isLoading: false,
          isEdit: false,
        };
        let tempTodos = [newTodo, ...todos];

        setTodos(tempTodos);
        togglehandler();
        setShowSpinner(false);
      }, 500);
    }
  };
  //toggle
  const togglehandler = (e) => {
    setTaskvalue("");
    setShow(!show);
  };
  //search toggle
  const SearchToggle = () => {
    if (search !== "") {
      setShowBigSpinner(true);
      setTimeout(() => {
        setSearch("");
        setShowBigSpinner(false);
      }, 500);
    }
    setSearchShow(!searchShow);
  };
  // useEffect
  useEffect(() => {
    setTodoLength(todos.length);
    const arr = [];
    if (flag === "all") {
      todos.map((todo) => {
        if (todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase()))
          arr.push(todo);
        // setShowTodos(arr);
      });
    } else if (flag === "incomplete") {
      todos.map((todo) => {
        if (
          todo.completed_on === null &&
          todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
        )
          arr.push(todo);
        // setShowTodos(arr);
      });
    } else {
      todos.map((todo) => {
        if (
          todo.completed_on !== null &&
          todo.name.toLocaleLowerCase().includes(search.toLocaleLowerCase())
        )
          arr.push(todo);
      });
    }
    if (todos.length === 0) setFlag("all");
    setShowTodos(arr);
  }, [flag, todos, search]);
  return (
    <div className="App">
      <Header
        SearchToggle={SearchToggle}
        searchShow={searchShow}
        searchvalue={searchvalue}
        showBigSpinner={showBigSpinner}
        todoLength={todoLength}
      />
      <Todos
        todos={showTodos}
        onDelete={deletetodo}
        completeHandler={completeHandler}
        show={show}
        togglehandler={togglehandler}
        addhandler={addhandler}
        task={task}
        showSpinner={showSpinner}
        showBigSpinner={showBigSpinner}
        todoLength={todoLength}
        flagHandler={flagHandler}
        editToggle={editToggle}
      />
      <div>
        {showBigSpinner && (
          <img className="spinning rotateFull" src={spin} alt="spin" />
        )}
      </div>
      {todoLength === 0 && (
        <div>
          <img src={emptyScreen} className="emptyScreen" alt="emptyScreen" />
          <label className="pleaseAdd">
            You didn't add any task. Please, add one.
          </label>
        </div>
      )}
      {todoLength !== 0 && showTodos.length === 0 && (
        <div>
          <img src={emptyScreen} className="emptyScreen" alt="emptyScreen" />
          <label className="pleaseAdd"> There is no data for {flag}</label>
        </div>
      )}
    </div>
  );
}

export default App;
