import "./App.css";
import { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "./Header";
import { v4 as uuidv4 } from "uuid";
function App() {
  const [todos, setTodos] = useState([
    {
      id: 1,
      name: "Hello",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
    },
    {
      id: 2,
      name: "Hola",
      created_at: "2022-03-31",
      completed_on: null,
      isLoading: false,
    },
    {
      id: 3,
      name: "dshdksgh",
      created_at: "2022-04-07",
      completed_on: "2022-04-07",
      isLoading: false,
    },
    {
      id: 4,
      name: "Running",
      created_at: "2022-03-30",
      completed_on: "2022-04-05",
      isLoading: false,
    },
  ]);
  const [show, setShow] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [taskvalue, setTaskvalue] = useState("");
  // Delete task
  const deletetodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };
  // on Complete

  const completeHandler = (id) => {
    const index = todos.findIndex((todo) => todo.id === id);
    let tempTodos = [...todos];
    tempTodos[index].isLoading = true;
    setTodos((prev) => tempTodos);
    setTimeout(() => {
      const d = new Date(Date.now()).toLocaleDateString();
      console.log(d);
      tempTodos = [...todos];
      tempTodos[index].completed_on = d;
      tempTodos[index].isLoading = false;
      setTodos((prev) => tempTodos);
    }, 1000);
  };
  // addhandler
  const addhandler = (e) => {
    console.log(taskvalue);
    if (taskvalue.length < 3) window.alert("less then 3");
    else {
      setShowSpinner(true);
      setTimeout(() => {
        const d = new Date(Date.now());
        let newTodo = {
          id: uuidv4(),
          name: taskvalue,
          created_at: `${d.getFullYear()} - ${d.getMonth()} - ${d.getDate()}`,
          completed_on: null,
          isLoading: false,
        };
        let tempTodos = [newTodo, ...todos];

        setTodos(tempTodos);
        togglehandler();
        setShowSpinner(false);
      }, 500);
    }
  };
  //toggle
  const togglehandler = (e) => {
    setTaskvalue("");
    setShow(!show);
  };

  return (
    <div className="App">
      <Header />
      <Todos
        todos={todos}
        onDelete={deletetodo}
        completeHandler={completeHandler}
        show={show}
        togglehandler={togglehandler}
        addhandler={addhandler}
        setTaskvalue={setTaskvalue}
        showSpinner={showSpinner}
      />
    </div>
  );
}

export default App;
