/* eslint-disable array-callback-return */

import { v4 as uuidv4 } from "uuid";
import React, { useState, useEffect } from "react";
import Todos from "../Todos";
import Header from "../Header";
import Icon from "../Icon";
import Tag from "../Tag";
import Toaster from "../Toaster";
import { supabase } from "../../config/apiClient";
export const AppContext = React.createContext();
function Home() {
  const [todos, setTodos] = useState([]);

  const [toasts, setToasts] = useState([]);
  const [showEmpty, setShowEmpty] = useState(true);
  const [showBigSpinner, setShowBigSpinner] = useState(false);
  const [searchShow, setSearchShow] = useState(false);
  const [flag, setFlag] = useState("all");
  const [search, setSearch] = useState("");
  const [dataCount, setDataCount] = useState(0);
  const [splash, setSplash] = useState(true);

  //flag
  const flagHandler = (e) => {
    if (flag !== e) {
      setFlag(e);
    }
  };

  //search value
  const searchvalue = (e) => {
    if (e.length > 2) {
      setTimeout(() => {
        setShowBigSpinner(true);
      }, 500);
      setTimeout(() => {
        setSearch(e);
        setShowBigSpinner(false);
      }, 1500);
    }
  };

  //search toggle
  const SearchToggle = () => {
    if (search !== "") {
      setShowBigSpinner(true);
      setTimeout(() => {
        setSearch("");
        setShowBigSpinner(false);
      }, 500);
    }
    setSearchShow(!searchShow);
  };
  //splash useEffect
  /* useEffect(() => {
    setTimeout(() => {
      setSplash(false);
    }, 1000);
  }, []); */
  // useEffect
  useEffect(() => {
    const fetchData = async (e) => {
      setShowBigSpinner(true);
      if (flag === "all") {
        const { data } = await supabase
          .from("ReactTodo")
          .select()
          .ilike("name", `%${search}%`)
          .order("id", { ascending: false });
        setTodos([]);
        // setDataCount(data.length);
        setTodos(data);
        if (splash) {
          setDataCount(data.length);
          setSplash(false);
        }
      } else if (flag === "incomplete") {
        const { data } = await supabase
          .from("ReactTodo")
          .select()
          .ilike("name", `%${search}%`)
          .is("completed_on", null)
          .order("id", { ascending: false });

        setTodos([]);
        setTodos(data);
      } else {
        const { data } = await supabase
          .from("ReactTodo")
          .select()
          .ilike("name", `%${search}%`)
          .order("id", { ascending: false })
          .not("completed_on", "is", null);

        setTodos([]);
        setTodos(data);
      }
      /*  if (todos === null || todos.length === undefined) {
        setFlag("all");
        setSearchShow(false);
      } */
      // console.log(todos.length);
      setShowBigSpinner(false);
    };
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [flag, search]);
  //Toast useeffect
  useEffect(() => {
    if (toasts.length > 0) {
      setTimeout(() => {
        let allToast = [...toasts];
        allToast.shift();
        setToasts(allToast);
      }, 500);
    }
  }, [toasts]);
  const handleRemoveTodo = async (id) => {
    try {
      // eslint-disable-next-line
      const { data } = await supabase
        .from("ReactTodo")
        .delete()
        .match({ id: id });
      let newToast = {
        id: uuidv4(),
        type: "success",
        message: "Task Deleted",
      };

      setToasts([...toasts, newToast]);
    } catch (error) {
      let newToast = {
        id: uuidv4(),
        type: "error",
        message: error,
      };
      setToasts([...toasts, newToast]);
    }
    removeCompleteFromIncomplete(id);
    if (dataCount === 1) {
      setSearchShow(false);
      setFlag("all");
    }
  };
  const removeCompleteFromIncomplete = (id) => {
    const filtertodos = todos.filter((todo) => todo.id !== id);
    setTodos(filtertodos);
  };
  return (
    <div className="App">
      {splash && (
        <div className="splash faddingOut">
          <div className="splashLogo">
            <div className="splashIconLogo">
              <Icon src="SplashLeaf" />
            </div>
            <div>
              <Icon src="SplashTitle" />
            </div>
          </div>
        </div>
      )}
      {!splash && (
        <div>
          <Header
            SearchToggle={SearchToggle}
            searchShow={searchShow}
            searchvalue={searchvalue}
            dataCount={dataCount}
          />
          <div className="overToaster">
            <div className="toaster">
              {toasts.length > 0 &&
                toasts.map((toast) => (
                  <Toaster
                    key={toast.id}
                    className={`faddingOut toast toast--visible toast--${toast.type}`}
                  >
                    {toast.type === "success" && <Icon src="Check" />}{" "}
                    {toast.message}
                  </Toaster>
                ))}
            </div>
          </div>
          <div className={`${showBigSpinner && "blur"}`}>
            <AppContext.Provider
              value={{
                todos,
                flag,
                flagHandler,
                toasts,
                setToasts,
                handleRemoveTodo,
                dataCount,
                setDataCount,
                setShowEmpty,
                removeCompleteFromIncomplete,
              }}
            >
              <Todos />
            </AppContext.Provider>
          </div>

          <div>
            {showBigSpinner && (
              <Icon className="spinning rotateFull" src="Spin" />
            )}
          </div>

          {showEmpty && dataCount === 0 && !showBigSpinner && (
            <div className={`emptyScreenOver `}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd">
                You didn't add any task. Please, add one.
              </Tag>
            </div>
          )}
          {showEmpty && dataCount > 0 && todos.length === 0 && !showBigSpinner && (
            <div className={`emptyScreenOver `}>
              <Icon src="EmptyScreen" className="emptyScreen" />
              <Tag className="pleaseAdd"> There is no data for {flag}</Tag>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Home;
